INSERT INTO `posts` (`post_id`, `cat_id`, `title`, `slug`, `body`, `keywords`, `image`, `status`, `created_at`, `updated_at`) VALUES
(1, 3, 'ঢাকা থেকে আসছেন ডাঃ জুবায়ের আহমদ', 'dr-jubayer-ahmed', '<p>#সিলেটবাসীরজন্যসুখবর-</p>\r\n\r\n<p>ঢাকা থেকে আগত দেশবরণ্য কোভিড-১৯ যোদ্ধা<br />\r\nডাঃ জুবায়ের আহমদ<br />\r\nমেডিসিন, এলার্জি ও রিউম্যাটোলজি বিশেষজ্ঞ<br />\r\nরোগী দেখবেন কমফোর্ট মেডিকেল সার্ভিসে।<br />\r\n১৮,১৯,২০ নভেম্বর সিরিয়ালের জন্য যোগাযোগ করুন-<br />\r\n📞 ০১৭১৫৯৭১৬৮৫.</p>\r\n\r\n<p>এছাড়াও আমাদের ওয়েবসাইটের মাধ্যমে অনলাইনে Appointment নেওয়ার সুবিধা রয়েছে।</p>\r\n\r\n<p>Link-<br />\r\nhttps://www.doctorebari.com/doctor/details/dr-jubayer-ahmed</p>', NULL, 'image/posts/YZ3Y7OSZTXNrY0RKmxWj7D00fziX6PmCXnIED1LZ.jpeg', 1, '2020-11-13 22:59:31', '2020-11-15 16:17:38'),
(2, 2, 'ডাঃ শিশির চক্রবর্তী স্যারের চেম্বার অনির্দিষ্টকালের জন্য চেম্বার বন্ধ', 'dr-shisir-chokroborty', '<p>বিশেষ বিজ্ঞপ্তিঃ সিলেট এমএজি ওসমানী মেডিকেল কলেজ হাসপাতাল এর মেডিসিন বিশেষজ্ঞ অধ্যাপক ডাঃ শিশির চক্রবর্তী স্যারের চেম্বার বন্ধ।</p>\r\n\r\n<p>সম্মানিত রোগীদের অবগতির জন্য জানানো যাচ্ছে যে বিশেষ কারণে চলিত সপ্তাহের চেম্বার বন্ধ থাকে এবং আগামী সপ্তাহে সিরিয়ালের নাম্বারে যোগাযোগ করে আসবেন।<br />\r\nসম্মানিত রোগীদের সাময়িক অসুবিধার জন্য আন্তরিকভাবে দুঃখিত।</p>\r\n\r\n<p>ধন্যবাদান্তেঃ অধ্যাপক ডাঃ শিশির চক্রবর্তী</p>\r\n\r\n<p>সূত্রঃ চেম্বারের প্রধান ফটক</p>', NULL, 'image/posts/zeq43YSwOLtTzKTDjX6nJ0PoFj3QBIc4gGNQvOT2.jpeg', 1, '2020-11-15 16:10:53', '2020-11-15 22:08:12'),
